--DROP VIEW RV_M_Transaction_Costing;CREATE OR REPLACE VIEW RV_M_Transaction_Costing ASSELECT t.M_Transaction_ID, t.AD_Client_ID,t.AD_Org_ID,t.MovementType,t.MovementDate,t.MovementQty,t.M_AttributeSetInstance_ID, t.M_AttributeSet_ID, t.SerNo, t.Lot, t.M_Lot_ID, t.GuaranteeDate,t.M_Product_ID,t.Value,t.Name,t.Description,t.UPC,t.SKU,t.C_UOM_ID,t.M_Product_Category_ID,t.Classification, t.Group1, t.Group2, t.Weight,t.Volume,t.VersionNo,t.DocumentNo,t.C_DocType_ID,t.M_Locator_ID, t.X, t.Y, t.Z,t.M_Warehouse_ID,t.M_InventoryLine_ID,t.M_Inventory_ID,t.M_MovementLine_ID,t.M_Movement_ID,t.M_InOutLine_ID,t.M_InOut_ID,t.M_ProductionLine_ID,t.M_ProductionPlan_ID,t.M_Production_ID,t.C_ProjectIssue_ID,t.C_Project_ID,t.PP_Cost_Collector_ID,cd.C_AcctSchema_ID,cd.M_CostType_ID,cd.M_CostElement_ID,cd.CostAdjustment,	cd.CostAdjustmentDate,cd.DateAcct,cd.CumulatedQty AS BeginningQtyBalance,cd.CurrentCostPrice,cd.CurrentCostPriceLL,cd.isReversal,cd.IsSOTrx,cd.M_CostDetail_ID,cd.CumulatedAmt AS BeginningBalance,cd.qty,cd.Amt, cd.CostAmt,cd.CumulatedQty + Qty AS EndingQtyBalance,cd.CumulatedAmt + CostAdjustment + Amt AS EndingBalanceFROM M_Product p INNER JOIN RV_Transaction t ON (t.M_Product_ID=p.M_Product_ID)LEFT OUTER JOIN M_CostDetail cd ON (cd.M_Transaction_ID=t.M_Transaction_ID AND cd.M_Product_ID=p.M_Product_ID) LEFT OUTER JOIN M_CostType ct ON (ct.M_CostType_ID=cd.M_CostType_ID)LEFT OUTER JOIN M_CostElement ce ON (ce.M_CostElement_ID=cd.M_CostElement_ID);